mapper.update:
UPDATE sys_claim_company
name = #{name}, organization_code = #{organizationCode}, register_number = #{registerNumber}, type = #{type}, corporate_name = #{corporateName}, corporate_cardnum = #{corporateCardnum}, establish_date = #{establishDate}, register_capital = #{registerCapital}, business_start_date = #{businessStartDate}, business_end_date = #{businessEndDate}, registration_authority = #{registrationAuthority}, address = #{address}, operate_status = #{operateStatus}, create_time = #{createTime}, note = #{note}, market_mark = #{marketMark}, mark_time = #{markTime}, mark_note = #{markNote}, operator_id = #{operatorId},
WHERE id = #{id}

mapper.select:
SELECT  FROM sys_claim_company
	
		 id = #{id} 
		 and name = #{name} 
		 and organization_code = #{organizationCode} 
		 and register_number = #{registerNumber} 
		 and type = #{type} 
		 and corporate_name = #{corporateName} 
		 and corporate_cardnum = #{corporateCardnum} 
		 and establish_date = #{establishDate} 
		 and register_capital = #{registerCapital} 
		 and business_start_date = #{businessStartDate} 
		 and business_end_date = #{businessEndDate} 
		 and registration_authority = #{registrationAuthority} 
		 and address = #{address} 
		 and operate_status = #{operateStatus} 
		 and create_time = #{createTime} 
		 and note = #{note} 
		 and market_mark = #{marketMark} 
		 and mark_time = #{markTime} 
		 and mark_note = #{markNote} 
		 and operator_id = #{operatorId}
